mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,CREATOR ,TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,CREATOR ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD WHERE BOARD_TYPE IN
#{item}
) WHERE NUMROW BETWEEN (#{pageVo.pageNo}-1)*10 + 1 AND (#{pageVo.pageNo})*10

mapper.update:
UPDATE
	BOARD
SET
	BOARD_TITLE = #{boardTitle},
	BOARD_COMMENT = #{boardComment},
	MODIFIER = #{creator}
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}

mapper.delete:
DELETE
FROM
	BOARD
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}
